});
function textFill(input){ //input focus text function  
  var originalvalue = input.val();  
  input.focus( function(){  
    if( $.trim(input.val()) == originalvalue ){
      input.val(''); 
    }  
  }).blur( function(){  
    if( $.trim(input.val()) == '' ){ 
      input.val(originalvalue); 
    }  
  });  
}
</script>
